    @Test
    public void testSerialize() {
        IPacket packet = new IPv4()
        .setDiffServ((byte) 0x20)
        .setIdentification((short) 0x1d85)
        .setFlags((byte) 0x00)
        .setTtl((byte) 50)
        .setSourceAddress("74.125.45.109")
        .setDestinationAddress("192.168.1.111")
        .setPayload(new TCP()
                        .setSourcePort(993)
                        .setDestinationPort(49202)
                        .setSequence(0xe3adee88)
                        .setAcknowledge(0xb7dad824)
                        .setDataOffset((byte) 8)
                        .setFlags((short) 0x10)
                        .setWindowSize((short) 267)
                        .setOptions(new byte[] {0x01, 0x01, 0x08, 0x0a, 0x20, (byte) 0x9a,
                                                0x41, 0x04, 0x07, 0x76, 0x53, 0x1f})
                        .setPayload(null)
                   );
        byte[] actual = packet.serialize();
        assertTrue(Arrays.equals(pktSerialized, actual));
    }
    
    @Test
    public void testDeserialize() throws PacketParsingException {
        IPacket packet = new IPv4();
        packet.deserialize(pktSerialized, 0, pktSerialized.length);
        byte[] pktSerialized1 = packet.serialize();
        assertTrue(Arrays.equals(pktSerialized, pktSerialized1));
    }
    
    @Test
    public void testChecksum() throws PacketParsingException{
    	Ethernet packet = new Ethernet();
    	
    	IPv4 ip = new IPv4();
    	ip.deserialize(fea8, 0, fea8.length);

    	TCP payload = (TCP)ip.getPayload();
    	payload.resetChecksum();
    	payload.serialize();
    	
    	assertTrue(payload.getChecksum() == (short)0xfea8);
    	
    	
    	packet.deserialize(checksumTestPacket1, 0, checksumTestPacket1.length);
    	payload = (TCP)packet.getPayload().getPayload();
    	payload.resetChecksum();
    	payload.serialize();
    	
    	assertTrue(payload.getChecksum() == (short)0xff03);
    	
    	packet = new Ethernet();
    	packet.deserialize(checksumTestPacket2, 0, checksumTestPacket2.length);
    	payload = (TCP)packet.getPayload().getPayload();
    	payload.resetChecksum();
    	payload.serialize();
    	
    	assertTrue(payload.getChecksum() == (short)0xff09);
    	
    	
    }
